// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract SolarTrade {
    // 太阳能板结构
    struct SolarPanel {
        uint256 id;
        address owner;
        uint256 price;
        uint256 lat;
        uint256 lng;
        uint256 batteryTemp;
        uint256 dcPower;
        uint256 acPower;
        bool isForSale;
    }

    IERC20 public token;
    address public owner;
    uint256 public panelCount = 0;
    mapping(uint256 => SolarPanel) public panels;
    mapping(uint256 => address[]) public ownershipHistory;

    // 添加事件
    event PanelCreated(uint256 indexed panelId, address indexed owner, uint256 price);
    event PanelBought(uint256 indexed panelId, address indexed newOwner, uint256 price);
    event PanelUpdated(uint256 indexed panelId, uint256 batteryTemp, uint256 dcPower, uint256 acPower);
    event PanelListedForSale(uint256 indexed panelId, uint256 price);

    // 添加错误信息
    error InsufficientAllowance(uint256 required, uint256 actual);
    error InsufficientBalance(uint256 required, uint256 actual);
    error InvalidPanel();
    error Unauthorized();

    modifier onlyOwner() {
        if (msg.sender != owner) revert Unauthorized();
        _;
    }

    modifier panelExists(uint256 _panelId) {
        if (_panelId == 0 || _panelId > panelCount) revert InvalidPanel();
        _;
    }

    constructor(address _tokenAddress) {
        // 检查代币地址是否有效
        require(_tokenAddress != address(0), "Invalid token address");
        owner = msg.sender;
        token = IERC20(_tokenAddress);
    }

    // 创建太阳能板（添加了检查）
    function createPanel(
        uint256 _lat,
        uint256 _lng,
        uint256 _batteryTemp,
        uint256 _dcPower,
        uint256 _acPower,
        uint256 _price
    ) public {
        // 检查代币授权
        uint256 allowance = token.allowance(msg.sender, address(this));
        if (allowance < _price) {
            revert InsufficientAllowance(_price, allowance);
        }

        // 检查余额
        uint256 balance = token.balanceOf(msg.sender);
        if (balance < _price) {
            revert InsufficientBalance(_price, balance);
        }

        // 转移代币
        require(token.transferFrom(msg.sender, owner, _price), "Token transfer failed");

        // 创建面板
        panelCount++;
        panels[panelCount] = SolarPanel(
            panelCount,
            msg.sender,
            _price,
            _lat,
            _lng,
            _batteryTemp,
            _dcPower,
            _acPower,
            true
        );

        ownershipHistory[panelCount].push(msg.sender);
        emit PanelCreated(panelCount, msg.sender, _price);
    }

    // 购买太阳能板（添加了检查）
    function buyPanel(uint256 _panelId) public panelExists(_panelId) {
        SolarPanel storage panel = panels[_panelId];
        require(panel.isForSale, "Panel not for sale");
        require(panel.owner != msg.sender, "Cannot buy your own panel");

        // 检查代币授权
        uint256 allowance = token.allowance(msg.sender, address(this));
        if (allowance < panel.price) {
            revert InsufficientAllowance(panel.price, allowance);
        }

        // 检查余额
        uint256 balance = token.balanceOf(msg.sender);
        if (balance < panel.price) {
            revert InsufficientBalance(panel.price, balance);
        }

        // 转移代币
        require(token.transferFrom(msg.sender, panel.owner, panel.price), "Token transfer failed");

        // 更新所有权
        panel.owner = msg.sender;
        panel.isForSale = false;
        ownershipHistory[_panelId].push(msg.sender);

        emit PanelBought(_panelId, msg.sender, panel.price);
    }

    // 更新太阳能板数据
    function updatePanelData(
        uint256 _panelId,
        uint256 _batteryTemp,
        uint256 _dcPower,
        uint256 _acPower
    ) public onlyOwner panelExists(_panelId) {
        SolarPanel storage panel = panels[_panelId];
        panel.batteryTemp = _batteryTemp;
        panel.dcPower = _dcPower;
        panel.acPower = _acPower;

        emit PanelUpdated(_panelId, _batteryTemp, _dcPower, _acPower);
    }

    // 获取太阳能板信息
    function getPanelInfo(uint256 _panelId) public view panelExists(_panelId) returns (
        address, uint256, uint256, uint256, uint256, uint256, uint256, bool
    ) {
        SolarPanel memory panel = panels[_panelId];
        return (
            panel.owner,
            panel.price,
            panel.lat,
            panel.lng,
            panel.batteryTemp,
            panel.dcPower,
            panel.acPower,
            panel.isForSale
        );
    }

    // 设置太阳能板为出售状态
    function setForSale(uint256 _panelId, uint256 _newPrice) public panelExists(_panelId) {
        SolarPanel storage panel = panels[_panelId];
        require(msg.sender == panel.owner, "Not panel owner");
        require(_newPrice > 0, "Price must be greater than 0");

        panel.isForSale = true;
        panel.price = _newPrice;

        emit PanelListedForSale(_panelId, _newPrice);
    }

    // 获取交易历史
    function getPanelHistory(uint256 _panelId) public view panelExists(_panelId) returns (address[] memory) {
        return ownershipHistory[_panelId];
    }

    // 紧急取回误转入的代币（安全功能）
    function emergencyTokenWithdraw(address _token) public onlyOwner {
        IERC20 tokenToWithdraw = IERC20(_token);
        uint256 balance = tokenToWithdraw.balanceOf(address(this));
        require(balance > 0, "No tokens to withdraw");
        require(tokenToWithdraw.transfer(owner, balance), "Transfer failed");
    }
}

