contracts/PowerReward.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// 引入 SolarPanels 合约接口
interface ISolarPanels {
    struct Panel {
        uint256 id;
        address owner;
        uint256 latitude;
        uint256 longitude;
        uint256 batteryTemperature;
        uint256 dcPower;
        uint256 acPower;
        uint256 createdAt;
        bool exists;
    }

    function getMyPanels() external view returns (Panel[] memory);
}

contract PowerReward {
    IERC20 public rewardToken;
    ISolarPanels public solarPanelContract;
    address public owner;

    mapping(address => bool) public hasClaimed;

    event RewardClaimed(address indexed user, uint totalPower, uint rewardAmount);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor(address _rewardToken, address _solarPanelContract) {
        rewardToken = IERC20(_rewardToken);
        solarPanelContract = ISolarPanels(_solarPanelContract);
        owner = msg.sender;
    }

    // 🚀 主功能：领取奖励
    function claimReward() public {
        require(!hasClaimed[msg.sender], "Already claimed");

        ISolarPanels.Panel[] memory panels = solarPanelContract.getMyPanels();
        require(panels.length > 0, "No panels found");

        uint totalPower = 0;

        for (uint i = 0; i < panels.length; i++) {
            totalPower += panels[i].acPower;
        }

        require(totalPower > 0, "No power output");

        uint rewardAmount = totalPower; // 1W = 1 token，可换成更复杂的公式
        hasClaimed[msg.sender] = true;

        require(rewardToken.transfer(msg.sender, rewardAmount), "Token transfer failed");

        emit RewardClaimed(msg.sender, totalPower, rewardAmount);
    }

    // 🔧 后备方法：合约充值（owner 发送奖励池）
    function deposit(uint amount) public onlyOwner {
        require(rewardToken.transferFrom(msg.sender, address(this), amount), "Deposit failed");
    }

    // 🔍 查询我的可领取奖励
    function previewReward() public view returns (uint) {
        if (hasClaimed[msg.sender]) return 0;

        ISolarPanels.Panel[] memory panels = solarPanelContract.getMyPanels();
        uint totalPower = 0;
        for (uint i = 0; i < panels.length; i++) {
            totalPower += panels[i].acPower;
        }
        return totalPower;
    }
}

contracts/Shop.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// 接入 SolarPanels 合约接口
interface ISolarPanels {
    function transferPanelOwnership(uint panelId, address newOwner) external;
    function getPanel(uint panelId) external view returns (
        address owner,
        uint latitude,
        uint longitude,
        uint batteryTemperature,
        uint dcPower,
        uint acPower,
        uint createdAt
    );
}

contract Shop {
    address public owner;
    uint public itemCount = 0;
    IERC20 public token; // 绑定 ERC20 代币
    ISolarPanels public solarPanelContract; // 外部 SolarPanels 合约

    struct Item {
        uint id;
        string name;
        uint price;
        address seller;
        bool purchased;
        uint latitude;
        uint longitude;
        uint batteryTemperature;
        uint dcPower;
        uint acPower;
        uint createdAt;
        uint panelId; // 新增：对应的太阳能板 ID
    }

    struct Purchase {
        uint itemId;
        address buyer;
        uint price;
        uint timestamp;
    }

    mapping(uint => Item) public items;
    mapping(address => Purchase[]) public purchaseHistory;

    event ItemListed(uint id, string name, uint price, address seller);
    event ItemPurchased(uint id, address buyer, uint price);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the contract owner");
        _;
    }

    modifier itemExists(uint _id) {
        require(_id > 0 && _id <= itemCount, "Item does not exist");
        _;
    }

    constructor(address _tokenAddress, address _solarPanelAddress) {
        require(_tokenAddress != address(0), "Invalid token address");
        require(_solarPanelAddress != address(0), "Invalid solar panel contract");
        owner = msg.sender;
        token = IERC20(_tokenAddress);
        solarPanelContract = ISolarPanels(_solarPanelAddress);
    }

    // 上架商品，并关联面板 ID
    function listItem(
        string memory _name,
        uint _price,
        uint _latitude,
        uint _longitude,
        uint _batteryTemperature,
        uint _dcPower,
        uint _acPower,
        uint _createdAt,
        uint _panelId // 新增参数
    ) public {
        require(_price > 0, "Price must be greater than zero");

        // 校验 seller 拥有该面板
        (address panelOwner,,,,,,) = solarPanelContract.getPanel(_panelId);
        require(panelOwner == msg.sender, "You do not own this panel");

        itemCount++;
        items[itemCount] = Item(
            itemCount,
            _name,
            _price,
            msg.sender,
            false,
            _latitude,
            _longitude,
            _batteryTemperature,
            _dcPower,
            _acPower,
            _createdAt,
            _panelId
        );

        emit ItemListed(itemCount, _name, _price, msg.sender);
    }

    // 购买并转移面板所有权
    function buyItem(uint _id) public itemExists(_id) {
        Item storage item = items[_id];
        require(!item.purchased, "Item already sold");
        require(token.balanceOf(msg.sender) >= item.price, "Insufficient token balance");
        require(token.allowance(msg.sender, address(this)) >= item.price, "Allowance too low");

        // 校验 panel 仍属于 seller
        (address panelOwner,,,,,,) = solarPanelContract.getPanel(item.panelId);
        require(panelOwner == item.seller, "Seller no longer owns the panel");

        require(token.transferFrom(msg.sender, item.seller, item.price), "Token transfer failed");

        // 转移所有权
        solarPanelContract.transferPanelOwnership(item.panelId, msg.sender);

        item.purchased = true;

        purchaseHistory[msg.sender].push(Purchase(_id, msg.sender, item.price, block.timestamp));

        emit ItemPurchased(_id, msg.sender, item.price);
    }

    function getItem(uint _id) public view itemExists(_id) returns (Item memory) {
        return items[_id];
    }

    function getPanelInfo(uint _id)
        public
        view
        itemExists(_id)
        returns (uint latitude, uint longitude, uint batteryTemperature, uint dcPower, uint acPower, uint createdAt)
    {
        Item memory item = items[_id];
        return (item.latitude, item.longitude, item.batteryTemperature, item.dcPower, item.acPower, item.createdAt);
    }

    function getPurchaseHistory(address _buyer) public view returns (Purchase[] memory) {
        return purchaseHistory[_buyer];
    }
}

contracts/SolarPanels.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract SolarPanels {
    struct Panel {
        uint256 id;
        address owner;
        uint256 latitude;
        uint256 longitude;
        uint256 batteryTemperature;
        uint256 dcPower;
        uint256 acPower;
        uint256 createdAt;
        bool exists;
    }

    uint256 public panelCount = 0;
    mapping(uint256 => Panel) public panels;
    mapping(address => uint256[]) public ownerPanels;

    event PanelCreated(
        uint256 indexed panelId,
        address indexed owner,
        uint256 latitude,
        uint256 longitude,
        uint256 batteryTemperature,
        uint256 dcPower,
        uint256 acPower,
        uint256 createdAt
    );

    event PanelUpdated(
        uint256 indexed panelId,
        uint256 batteryTemperature,
        uint256 dcPower,
        uint256 acPower
    );

    modifier onlyOwner(uint256 _panelId) {
        require(panels[_panelId].owner == msg.sender, "Not the owner of this panel");
        _;
    }

    function createPanel(
        uint256 _latitude,
        uint256 _longitude,
        uint256 _batteryTemperature,
        uint256 _dcPower,
        uint256 _acPower
    ) public {
        panelCount++;
        panels[panelCount] = Panel({
            id: panelCount,
            owner: msg.sender,
            latitude: _latitude,
            longitude: _longitude,
            batteryTemperature: _batteryTemperature,
            dcPower: _dcPower,
            acPower: _acPower,
            createdAt: block.timestamp,
            exists: true
        });

        ownerPanels[msg.sender].push(panelCount);

        emit PanelCreated(
            panelCount,
            msg.sender,
            _latitude,
            _longitude,
            _batteryTemperature,
            _dcPower,
            _acPower,
            block.timestamp
        );
    }

    function updatePanel(
        uint256 _panelId,
        uint256 _batteryTemperature,
        uint256 _dcPower,
        uint256 _acPower
    ) public onlyOwner(_panelId) {
        require(panels[_panelId].exists, "Panel does not exist");

        panels[_panelId].batteryTemperature = _batteryTemperature;
        panels[_panelId].dcPower = _dcPower;
        panels[_panelId].acPower = _acPower;

        emit PanelUpdated(_panelId, _batteryTemperature, _dcPower, _acPower);
    }

    function getPanel(uint256 _panelId) public view returns (
        address owner,
        uint256 latitude,
        uint256 longitude,
        uint256 batteryTemperature,
        uint256 dcPower,
        uint256 acPower,
        uint256 createdAt
    ) {
        require(panels[_panelId].exists, "Panel does not exist");

        Panel memory panel = panels[_panelId];
        return (
            panel.owner,
            panel.latitude,
            panel.longitude,
            panel.batteryTemperature,
            panel.dcPower,
            panel.acPower,
            panel.createdAt
        );
    }

    function getAllPanels() public view returns (Panel[] memory) {
        Panel[] memory allPanels = new Panel[](panelCount);
        for (uint256 i = 1; i <= panelCount; i++) {
            allPanels[i - 1] = panels[i];
        }
        return allPanels;
    }

    function getMyPanels() public view returns (Panel[] memory) {
        uint256[] memory myPanelIds = ownerPanels[msg.sender];
        Panel[] memory myPanels = new Panel[](myPanelIds.length);
        for (uint256 i = 0; i < myPanelIds.length; i++) {
            myPanels[i] = panels[myPanelIds[i]];
        }
        return myPanels;
    }
}

contracts/SolarToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract SolarToken is ERC20, Ownable {
    uint256 public cap = 100000000 * 10 ** 18; // 1 亿 SOLR 作为总量上限

    constructor() ERC20("SolarToken", "SOLR") Ownable(msg.sender) {
        _mint(msg.sender, 1000000 * 10 ** 18); // 100 万 SOLR 初始供应
    }

    // 仅限合约拥有者（管理员）可以铸造新的 SOLR
    function mint(address to, uint256 amount) external onlyOwner {
        require(totalSupply() + amount <= cap, "over limit");
        _mint(to, amount);
    }

    // 允许销毁 SOLR 代币
    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }
}

